import React, { useState, useEffect, useRef } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import styled, { createGlobalStyle } from 'styled-components';
import MixtapeGenerator from './components/MixtapeGenerator';
import Login from './components/Login';
import Signup from './components/Signup';
import { AuthProvider, useAuth } from './context/AuthContext';
import { FastAverageColor } from 'fast-average-color';

const moodColors = {
  Energetic: '#ffb347',
  Chill: '#6dd5ed',
  Sad: '#b993d6',
  Happy: '#f7971e',
  Dark: '#232526',
  Pop: '#ff6a88',
  Rock: '#232526',
  Jazz: '#43cea2',
  Classical: '#f8ffae',
  Default: '#764ba2',
};

const GlobalStyle = createGlobalStyle`
  * {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
  }

  body {
    font-family: 'Inter', -apple-system, BlinkMacSystemFont, 'Segoe UI', 'Roboto', sans-serif;
    min-height: 100vh;
    color: #333;
    background: ${({ bgGradient }) => bgGradient || 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)'};
    transition: background 1.2s cubic-bezier(0.22, 1, 0.36, 1);
    position: relative;
    overflow-x: hidden;
  }

  body::before {
    content: '';
    position: fixed;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: ${({ bgGradient }) => bgGradient || 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)'};
    z-index: -1;
    animation: ${({ isPlaying }) => isPlaying ? 'pulse 0.8s ease-in-out infinite' : 'none'};
    transform: scale(${({ isPlaying }) => isPlaying ? 1.02 : 1});
    filter: blur(${({ isPlaying }) => isPlaying ? 1 : 0}px);
  }

  @keyframes pulse {
    0%, 100% {
      transform: scale(1) rotate(0deg);
    }
    50% {
      transform: scale(1.05) rotate(1deg);
    }
  }

  @keyframes wave {
    0% {
      transform: translateX(-100%) rotate(0deg);
    }
    100% {
      transform: translateX(100vw) rotate(360deg);
    }
  }

  body::after {
    content: '';
    position: fixed;
    top: 50%;
    left: -50px;
    width: 100px;
    height: 100px;
    background: radial-gradient(circle, rgba(255,255,255,0.3) 0%, transparent 70%);
    border-radius: 50%;
    z-index: -1;
    animation: ${({ isPlaying }) => isPlaying ? 'wave 4s linear infinite' : 'none'};
    animation-delay: ${({ isPlaying }) => isPlaying ? 1 : 0}s;
  }

  button {
    cursor: pointer;
    border: none;
    outline: none;
  }

  input {
    outline: none;
  }
`;

const AppContainer = styled.div`
  min-height: 100vh;
  display: flex;
  flex-direction: column;
`;

const ProtectedRoute = ({ children }) => {
  const { user } = useAuth();
  return user ? children : <Navigate to="/login" />;
};

function AppRoutes({
  currentSong,
  currentSongIndex,
  setCurrentSongIndex,
  mixtapeQueue,
  setMixtapeQueue,
  analysis,
  setAnalysis
}) {
  const { user } = useAuth();

  return (
    <AppContainer>
      <Routes>
        <Route 
          path="/" 
          element={
            <ProtectedRoute>
              <MixtapeGenerator
                currentSong={currentSong}
                currentSongIndex={currentSongIndex}
                setCurrentSongIndex={setCurrentSongIndex}
                mixtapeQueue={mixtapeQueue}
                setMixtapeQueue={setMixtapeQueue}
                analysis={analysis}
                setAnalysis={setAnalysis}
                setIsPlaying={setIsPlaying}
              />
            </ProtectedRoute>
          } 
        />
        <Route 
          path="/login" 
          element={user ? <Navigate to="/" /> : <Login />} 
        />
        <Route 
          path="/signup" 
          element={user ? <Navigate to="/" /> : <Signup />} 
        />
      </Routes>
    </AppContainer>
  );
}

function App() {
  const [mixtapeQueue, setMixtapeQueue] = useState([]);
  const [currentSongIndex, setCurrentSongIndex] = useState(0);
  const [analysis, setAnalysis] = useState(null);
  const [bgGradient, setBgGradient] = useState('linear-gradient(135deg, #667eea 0%, #764ba2 100%)');
  const [isPlaying, setIsPlaying] = useState(false);
  const facRef = useRef(new FastAverageColor());

  const currentSong = mixtapeQueue[currentSongIndex];

  useEffect(() => {
    async function updateBg() {
      let dominant = [102, 126, 234]; // default
      if (currentSong && currentSong.album_art_url) {
        try {
          const img = new window.Image();
          img.crossOrigin = 'Anonymous';
          img.src = currentSong.album_art_url;
          await new Promise((resolve, reject) => {
            img.onload = resolve;
            img.onerror = reject;
          });
          const color = facRef.current.getColor(img).value;
          dominant = color;
        } catch (e) {
          // fallback to default
        }
      }
      // Use AI mood/genre for secondary color
      let mood = analysis?.mood || analysis?.genre || 'Default';
      let moodColor = moodColors[mood] || moodColors.Default;

      setBgGradient(
        `linear-gradient(135deg, rgb(${dominant.join(',')}), ${moodColor} 100%)`
      );
    }
    updateBg();
    // eslint-disable-next-line
  }, [currentSong, analysis]);

  // Cleanup on unmount
  useEffect(() => {
    return () => {
      // Cleanup any resources if needed
    };
  }, []);

  return (
    <AuthProvider>
      <GlobalStyle bgGradient={bgGradient} isPlaying={isPlaying} />
      <Router>
        <AppRoutes
          currentSong={currentSong}
          currentSongIndex={currentSongIndex}
          setCurrentSongIndex={setCurrentSongIndex}
          mixtapeQueue={mixtapeQueue}
          setMixtapeQueue={setMixtapeQueue}
          analysis={analysis}
          setAnalysis={setAnalysis}
        />
      </Router>
    </AuthProvider>
  );
}

export default App;
