import React, { useState, useEffect, useRef } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import styled, { createGlobalStyle } from 'styled-components';
import MixtapeGenerator from './components/MixtapeGenerator';
import Login from './components/Login';
import Signup from './components/Signup';
import { AuthProvider, useAuth } from './context/AuthContext';
import { FastAverageColor } from 'fast-average-color';

const moodColors = {
  Energetic: '#ffb347',
  Chill: '#6dd5ed',
  Sad: '#b993d6',
  Happy: '#f7971e',
  Dark: '#232526',
  Pop: '#ff6a88',
  Rock: '#232526',
  Jazz: '#43cea2',
  Classical: '#f8ffae',
  Default: '#764ba2',
};

const GlobalStyle = createGlobalStyle`
  * {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
  }

  body {
    font-family: 'Inter', -apple-system, BlinkMacSystemFont, 'Segoe UI', 'Roboto', sans-serif;
    min-height: 100vh;
    color: #333;
    background: ${({ bgGradient }) => bgGradient || 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)'};
    transition: background 1.2s cubic-bezier(0.22, 1, 0.36, 1);
  }

  button {
    cursor: pointer;
    border: none;
    outline: none;
  }

  input {
    outline: none;
  }
`;

const AppContainer = styled.div`
  min-height: 100vh;
  display: flex;
  flex-direction: column;
`;

const ProtectedRoute = ({ children }) => {
  const { user } = useAuth();
  return user ? children : <Navigate to="/login" />;
};

function AppRoutes({
  currentSong,
  currentSongIndex,
  setCurrentSongIndex,
  mixtapeQueue,
  setMixtapeQueue,
  analysis,
  setAnalysis
}) {
  const { user } = useAuth();

  return (
    <AppContainer>
      <Routes>
        <Route 
          path="/" 
          element={
            <ProtectedRoute>
              <MixtapeGenerator
                currentSong={currentSong}
                currentSongIndex={currentSongIndex}
                setCurrentSongIndex={setCurrentSongIndex}
                mixtapeQueue={mixtapeQueue}
                setMixtapeQueue={setMixtapeQueue}
                analysis={analysis}
                setAnalysis={setAnalysis}
              />
            </ProtectedRoute>
          } 
        />
        <Route 
          path="/login" 
          element={user ? <Navigate to="/" /> : <Login />} 
        />
        <Route 
          path="/signup" 
          element={user ? <Navigate to="/" /> : <Signup />} 
        />
      </Routes>
    </AppContainer>
  );
}

function App() {
  const [mixtapeQueue, setMixtapeQueue] = useState([]);
  const [currentSongIndex, setCurrentSongIndex] = useState(0);
  const [analysis, setAnalysis] = useState(null);
  const [bgGradient, setBgGradient] = useState('linear-gradient(135deg, #667eea 0%, #764ba2 100%)');
  const [audioIntensity, setAudioIntensity] = useState(0);
  const [isPlaying, setIsPlaying] = useState(false);
  const facRef = useRef(new FastAverageColor());
  const audioContextRef = useRef(null);
  const analyserRef = useRef(null);
  const animationFrameRef = useRef(null);

  const currentSong = mixtapeQueue[currentSongIndex];

  // Audio analysis setup
  useEffect(() => {
    if (currentSong && currentSong.preview_url && currentSong.preview_url !== 'null') {
      setupAudioAnalysis();
    } else {
      // Reset audio analysis when no preview available
      setAudioIntensity(0);
      setIsPlaying(false);
      if (animationFrameRef.current) {
        cancelAnimationFrame(animationFrameRef.current);
      }
    }
  }, [currentSong]);

  const setupAudioAnalysis = async () => {
    try {
      if (audioContextRef.current) {
        audioContextRef.current.close();
      }

      audioContextRef.current = new (window.AudioContext || window.webkitAudioContext)();
      const response = await fetch(currentSong.preview_url);
      const arrayBuffer = await response.arrayBuffer();
      const audioBuffer = await audioContextRef.current.decodeAudioData(arrayBuffer);

      const source = audioContextRef.current.createBufferSource();
      source.buffer = audioBuffer;

      analyserRef.current = audioContextRef.current.createAnalyser();
      analyserRef.current.fftSize = 256;
      analyserRef.current.smoothingTimeConstant = 0.8;

      source.connect(analyserRef.current);
      analyserRef.current.connect(audioContextRef.current.destination);

      // Start audio analysis
      startAudioAnalysis();
    } catch (error) {
      console.error('Audio analysis setup failed:', error);
    }
  };

  const startAudioAnalysis = () => {
    const analyseAudio = () => {
      if (!analyserRef.current) return;

      const bufferLength = analyserRef.current.frequencyBinCount;
      const dataArray = new Uint8Array(bufferLength);
      analyserRef.current.getByteFrequencyData(dataArray);

      // Calculate audio intensity (average of frequency data)
      const average = dataArray.reduce((a, b) => a + b) / bufferLength;
      const intensity = average / 255; // Normalize to 0-1

      setAudioIntensity(intensity);
      animationFrameRef.current = requestAnimationFrame(analyseAudio);
    };

    analyseAudio();
  };

  useEffect(() => {
    async function updateBg() {
      let dominant = [102, 126, 234]; // default
      if (currentSong && currentSong.album_art_url) {
        try {
          const img = new window.Image();
          img.crossOrigin = 'Anonymous';
          img.src = currentSong.album_art_url;
          await new Promise((resolve, reject) => {
            img.onload = resolve;
            img.onerror = reject;
          });
          const color = facRef.current.getColor(img).value;
          dominant = color;
        } catch (e) {
          // fallback to default
        }
      }
      // Use AI mood/genre for secondary color
      let mood = analysis?.mood || analysis?.genre || 'Default';
      let moodColor = moodColors[mood] || moodColors.Default;

      // Create dynamic background based on audio intensity
      const intensity = audioIntensity;
      const pulseScale = 1 + (intensity * 0.3); // Scale from 1 to 1.3
      const waveOffset = intensity * 50; // Wave offset based on intensity

      setBgGradient(
        `linear-gradient(135deg, rgb(${dominant.join(',')}), ${moodColor} 100%)`
      );
    }
    updateBg();
    // eslint-disable-next-line
  }, [currentSong, analysis, audioIntensity]);

  // Cleanup on unmount
  useEffect(() => {
    return () => {
      if (animationFrameRef.current) {
        cancelAnimationFrame(animationFrameRef.current);
      }
      if (audioContextRef.current) {
        audioContextRef.current.close();
      }
    };
  }, []);

  return (
    <AuthProvider>
      <GlobalStyle bgGradient={bgGradient} />
      <Router>
        <AppRoutes
          currentSong={currentSong}
          currentSongIndex={currentSongIndex}
          setCurrentSongIndex={setCurrentSongIndex}
          mixtapeQueue={mixtapeQueue}
          setMixtapeQueue={setMixtapeQueue}
          analysis={analysis}
          setAnalysis={setAnalysis}
        />
      </Router>
    </AuthProvider>
  );
}

export default App;
