import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import axios from 'axios';
import { useAuth } from '../context/AuthContext';
import MusicPlayer from './MusicPlayer';
import SongQueue from './SongQueue';

const Container = styled.div`
  min-height: 100vh;
  padding: 20px;
  max-width: 1200px;
  margin: 0 auto;
`;

const Header = styled.header`
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: 40px;
  padding: 20px;
  background: rgba(255, 255, 255, 0.95);
  backdrop-filter: blur(10px);
  border-radius: 20px;
  box-shadow: 0 10px 30px rgba(0, 0, 0, 0.1);
`;

const Logo = styled.h1`
  font-size: 2rem;
  font-weight: 800;
  color: #667eea;
  margin: 0;
`;

const UserInfo = styled.div`
  display: flex;
  align-items: center;
  gap: 15px;
`;

const Username = styled.span`
  color: #333;
  font-weight: 600;
`;

const LogoutButton = styled.button`
  background: #e74c3c;
  color: white;
  padding: 8px 16px;
  border-radius: 10px;
  font-size: 0.9rem;
  font-weight: 600;
  transition: background 0.2s ease;

  &:hover {
    background: #c0392b;
  }
`;

const MainContent = styled.div`
  display: grid;
  grid-template-columns: 1fr 1fr;
  gap: 30px;

  @media (max-width: 768px) {
    grid-template-columns: 1fr;
  }
`;

const GeneratorSection = styled.div`
  background: rgba(255,255,255,0.85);
  border-radius: 20px;
  box-shadow:
    0 4px 24px 0 rgba(102,126,234,0.18),
    0 1.5px 6px 0 rgba(118,75,162,0.10),
    0 0.5px 1.5px 0 rgba(0,0,0,0.08);
  border: 2.5px solid #e6e6f7;
  transition:
    transform 0.32s cubic-bezier(0.22, 1, 0.36, 1),
    box-shadow 0.32s cubic-bezier(0.22, 1, 0.36, 1),
    border-color 0.32s;
  will-change: transform, box-shadow, border-color;
  cursor: pointer;
  backdrop-filter: blur(8px);
  padding: 30px;
  &:hover, &:focus {
    transform: scale(1.045) translateY(-3px);
    box-shadow:
      0 12px 48px 0 rgba(118,75,162,0.22),
      0 4px 16px 0 rgba(102,126,234,0.18);
    border-color: #764ba2;
    background: rgba(255,255,255,0.93);
  }
`;

